fractal/magnet_1.source:
//Magnet: z = ((z^b + c - 1) / (b*z + c - 2))^a
//z(n+1) = 
@znext = expression(((z^q+c-1) / (q*z+c-2))^p);
//Power (p)
@p = real(2);
//Power (q)
@q = real(2);
//Bailout Test
@bailtest = bailout_function(norm);
//Escape
@escape = real(100);
//Converge
@converge = real(0.00005);

converged:
  //Converge test
  converged = (bailtest(z-(1,0)) < converge);

fractal/magnet_2.source:
