@znext = expression(((z^3 + 3(c-1).z + (c-1)(c-2)) / (3.z^2 + 3(c-2).z + (c-1)(c-2) + 1))^p);
//Power (p)
@p = real(2);
//Bailout Test
@bailtest = bailout_function(norm);
//Escape
@escape = real(100);
//Converge
@converge = real(0.00005);

converged:
  //Converge test
  converged = (bailtest(z-(1,0)) < converge);

