fractal/nova.source:
//Nova: z = z - (relax * (z^p - 1)) / (p * z^(p-1)) + c
//z(n+1) = 
@znext = expression(z - (relax*(z^p-1)) / (p*z^(p-1)) + c);
//Power (p)
@p = real(3);
//Relax
@relax = complex(1,0);
//Bailout Test
@bailtest = bailout_function(norm);
//Converge
@converge = real(0.00001);

converged:
  converged = (bailtest(z-z_1) < converge);

//Reset, called immediately before iterating
reset:
  //Nova init
  if (!julia && !perturb) z = (1,0);

fractal/cactus.source:
//z(n+1) = 
@znext = expression(z^p + z*(point-1) - point);
//Power (p)
@p = real(3);
//Escape
@escape = real(4);
//Bailout Test
@bailtest = bailout_function(norm);

//Reset, called immediately before iterating
reset:
  //Reset fractal, standard doesn't work with this algorithm
  z = point;
  if (julia)
    z = selected;
  else
    if (perturb) z = (0,0);

