colour/default.source:
//Default colouring algorithm
//  Applies iteration count and repeat parameter 
//  to get a colour from the gradient palette

//Palette repeat
@:repeat = real(1);

result:
  colour = gradient(:repeat * real(count) / real(limit));

colour/smooth.source:
//Smooth colouring algorithm
//Palette repeat
@:repeat = real(1);
//Power
@:power = expression(p);
//Bailout
@:bailout = expression(escape);
//Log function
@:lfn = real_function(log);

//Inverse log of (power)
real :il = 1.0/:lfn(:power);
real :lp = :lfn(:lfn(@:bailout));

result:
  real mu = real(count) + (:lp - :lfn(:lfn(cabs(z)))) * :il;
  colour = gradient(:repeat * mu / real(limit));

colour/exponential_smoothing.source:
//Exponential smoothing
//Palette repeat
@:repeat = real(1);
//Divergent
@:diverge = bool(true);
//Convergent
@:converge = bool(false);

real :sum = 0.0;
real :csum = 0.0;

calc:
  if (:diverge)
    :sum += exp(-cabs(z));

  if (:converge)
    :csum += exp(-1.0/cabs(z_1 - z));  

result:
  real mu = :sum;
  if (!:diverge || :converge && converged)
    mu =  :csum;

  colour = gradient(:repeat * mu / real(limit));

colour/triangle_inequality.source:
//Triangle Inequality
//Palette repeat
@:repeat = real(1);
//Power
@:power = expression(p);
//Bailout
@:bailout = expression(escape);
//Log function
@:lfn = real_function(log);

real :il = 1.0/:lfn(@:power);  //Inverse log of (power)
real :lp = :lfn(:lfn(@:bailout)/2.0);
real :sum = 10.0;
real :sum2 = 5.0;
real :ac = 0.0;

reset:
  :sum = 0.0;
  :sum2 = 0.0;
  :ac = cabs(c);

calc:
  :sum2 = :sum;
  if (count > 1)
  {
    real az2 = cabs(z - c);
    real lowbound = abs(az2 - :ac);
    :sum += (cabs(z) - lowbound) / (az2 + :ac - lowbound);
  }

result:
  :sum /= real(count);
  :sum2 /= real(count-1);
  real f = :il * (:lp - :lfn(:lfn(cabs(z))));
  real idx = :sum2 + (:sum - :sum2) * (f+1.0);
  colour = gradient(:repeat * idx);

colour/orbit_traps.source:
//Orbit traps

//Palette repeat
@:repeat = real(1);

real :closest = 1000000.0;

calc:
  real d = cabs(z);
  if (d < :closest) :closest = d;  

result:
  colour = gradient(:repeat * :closest);

colour/gaussian_integers.source:
//Gaussian integers
//Palette repeat
@:repeat = real(1);
//Round Mode
@:rmode = define('round|trunc|floor|ceil');
//Colour by
@:colourby = list('min dist|iteration @ min|angle @ min|max dist|iteration @ max|angle @ max|avg dist|min/mean/max angle|max/min ratio');
//Normalisation
@:normalise = list('none|point|factor|f(z)');
//Norm factor
@:normfact = complex(1,0);
//Norm function
@:normfunc = complex_function(ident);

real :r;
real :rmin = 1.0e12;
real :rmax = 0.0;
real :rave = 0.0;
real :total = 0.0;
real :itermin = 0.0;
real :itermax = 0.0;

complex :zmin = (0,0);
complex :zmax = (0,0);
complex :normfac;

init:
  if (:normalise == 0)
    :normfac = (1,0);      //no normalisation
  if (:normalise == 1)
    :normfac = point;        //point normalisation
  if (:normalise == 2)
    :normfac = :normfact;    //factor normalisation
  if (:normalise == 3)
    :normfac = :normfunc(z); //f(z) normalisation

calc:
  //Round Mode = round/truncate/floor/ceiling
  :r = cabs(z - mul(:rmode(div(z,:normfac)), :normfac));

  :total = :total + :r;
  :rave = :total / real(count);
  if (:r < :rmin)
  {
     :rmin = :r;
     :zmin = z;
     :itermin = real(count+1);
  }
  if (:r > :rmax)
  {
     :rmax = :r;
     :zmax = z;
     :itermax = real(count+1);
  }

result:
  real index, t;

  // minimum distance
  if (:colourby == 0)
    index = :rmin;
  // iteration @ min
  if (:colourby == 1)
    index = 0.01 * :itermin;
  // angle @ min
  if (:colourby == 2)
  {
    t = arg(:zmin);
    t = t/PI;
    if (t < 0.0) t = t + 2.0;
    index = 0.5 * t;
  }
  // maximum distance  
  if (:colourby == 3)
    index = :rmax;
  // iteration @ max
  if (:colourby == 4)
    index = 0.01 * :itermax;
  // angle @ max
  if (:colourby == 5)
  {
    t = arg(:zmax);
    t = t/PI;
    if (t < 0.0) t = t + 2.0;
    index = 0.5 * t;
  }
  // average distance
  if (:colourby == 6)
    index = :rave;
  // min/mean/max angle
  if (:colourby == 7)
  {
     :zmax = complex(:rave - :rmin, :rmax - :rave);
     t = arg(:zmax);
     t = t/PI;
     if (t < 0.0) t = t + 2.0;
     index = 0.5 * t;
  }
  // max/min ratio
  if (:colourby == 8)
    index = :rmax / (:rmin + 1.e-12);

  colour = gradient(:repeat * index);

colour/hot_and_cold.source:
//Hot & Cold
// 2 colors - on each orbit, the closer we are to 0, the more hot,
// the closer we are to bailout, the more cold. Compute an average 
// over all iterations 

//Bailout
@:bailout = real(4);
//Far distance
@:fardist = real(1.5);
//Close distance
@:closedist = real(1.0);
//Hot colour
@:hotcolour = rgba(255, 120, 5, 1.0);
//Cold colour
@:coldcolour = rgba(127, 200, 250, 1.0);

int :closecalls = 0;
int :farcalls = 0;

calc:
  real dist = norm(z);
  if (dist < @:closedist)
    :closecalls++;
  else if (@:bailout - dist < @:fardist)
    :farcalls++;

result:
  //return gradient(repeat * real(:closecalls) / real(count));
  rgba hcolour = @:hotcolour * (real(:closecalls)/real(count));
  rgba ccolour = @:coldcolour * (real(:farcalls)/real(count));
  colour = hcolour + ccolour;
